package example;

public class Day05_EX5 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub

		int nu = 3; 
		System.out.println(nu++); // 3 --> 4
		
		int n = 10; 
		System.out.println(++n); // 11
		
		int num = 3; 
		num++;
		// num = num + 1; 증가 연산자
		System.out.println(num);
		
		++num; // num = num + 1;
		System.out.println(num);
		
		
	}

}
